<script>
export default {
  name: "startscreen",
  props: {
    level: {
      type: Number,
      default: 1
    }
  },
  data() {
    return {
      marginLeftRbmg: 0,
      slide: 0
    };
  },
  mounted() {
    window.addEventListener("keydown", this.emitOnKey);
  },
  beforeDestroy() {
    window.removeEventListener("keydown", this.emitOnKey);
  },
  methods: {
    emitOnKey() {
      if (event.keyCode === 13) {
        this.$emit("startPreview");
      }
    },
    hideVideo() {
      this.marginLeftRbmg = "-100vw";
    }
  }
};
